function formatSlug(url) {
    // 从 URL 中提取最后一部分
    const slug = decodeURIComponent(url.split('/').pop());

    // 去掉扩展名和最后的 -数字
    const baseSlug = slug.replace(/-\d+\.html$/, '');

    // 将连字符替换为斜杠
    const formattedSlug = baseSlug.replace(/-/g, '/');

    return formattedSlug;
}  





